function emptySlide(){slide_wrap.empty()}function generatePagination(e){var t=$("#slide-pagination ul");t.append('<li><a href="#slide-1">&laquo;</a></li>');for(var n=1;n<=e;n++)t.append('<li><a href="#slide-'+n+'">'+n+"</a></li>");t.append('<li><a href="#slide-'+e+'">&raquo;</a></li>')}var slide_wrap=$(".slide-holder");$(function(){generatePagination(15);$("#hello").click(function(){emptySlide();slide_wrap.load("slides/hello.html")});$("#code").click(function(){emptySlide();slide_wrap.load("slides/code.html")});$("#docs").click(function(){emptySlide();slide_wrap.load("slides/docs.html")});$("#slide-pagination a").click(function(){emptySlide();var e=this.hash.substr(1);slide_wrap.load("slides/"+e+".html")});if(window.location.hash){var e=window.location.hash.substr(1);emptySlide();slide_wrap.load("slides/"+e+".html")}});